import { useEthers } from "@usedapp/core";
import { Button, makeStyles } from "@material-ui/core"
import rejoicingman from "../rejoicing-main.png"
import { useContractFunction } from "@usedapp/core";
import lotteryContract from "../App"
const useStyles = makeStyles((theme) => ({
    green: {
        background: "#00FF2A"
    },
    pink: {
        background: "#FF0077"
    },
    container: {
        padding: "72px 0px 72px 72px",
        display: "flex",
        justifyContent: "flex-start",
        gap: theme.spacing(1)
    }
}))
export const Header = () => {
    const classes = useStyles()
    const { activateBrowserWallet, deactivate, account } = useEthers()
    // const doit = () => {
    //     const { state, send } = useContractFunction(lotteryContract, 'enter', { transactionName: 'Wrap' })
    //     console.log(state, "||||", send, "||||||||")
    // }

    const isConnected = account != undefined

    return (
        <div className={classes.container}>


            <div className={"full-width"}>

                {isConnected ? <button color="green" className={"btn connect-btn connected"} onClick={deactivate}>
                    Connected


                </button> :
                    <button color="primary" className={"btn connect-btn"} onClick={activateBrowserWallet}>
                        Connect wallet


                    </button>
                }

                {<div className={"header-hero"}>
                    <div className={"header-hero-item1"}>
                        You can enter with atleast 1 usd worth of usd

                    </div>
                    <div className={"header-hero-item2"}>
                        <div>
                            Enter <br /> Lottery
                        </div>


                    </div>
                    <div className={"header-hero-item3"}>
                        <img src={rejoicingman} width={"500px"} height={"400px"} >
                        </img>
                    </div>

                </div>}
            </div>
        </div>
    )
}
