// import logo from './logo.svg';
import './App.css';
import Menu from './components/Menu';
import { Header } from './components/Header';
import RecentWinners from './components/RecentWinners';
import Web3 from 'web3';
import abi from "./abi/lottery.json"
import { useEffect, useState } from 'react';
const INFURA_ENDPOINT = "https://rinkeby.infura.io/v3/c95d9276d5e041acbe6411cdda48275e"
const LOTTERY_CONTRACT_ADDREESS = "0x65fd425aD3f20835662A9447677A7df40055EF68"
function App() {

  const LotteryContractAddress = '0x65fd425aD3f20835662A9447677A7df40055EF68'
  const [recentWinners, setrecentWinners] = useState([]);

  useEffect(() => {
    const web3 = new Web3(INFURA_ENDPOINT)
    const lotteryContract = new web3.eth.Contract(abi, LotteryContractAddress)
    console.log('attempting to retrieve recent winners')
    lotteryContract.methods.lottery_count().call().then((lotteryCount) => {
      console.log('Lottery Count: ' + lotteryCount)
      const recentWinnerstemp = []
      var countTemp = lotteryCount
      while (lotteryCount > 0) {
        lotteryContract.methods.recentWinners(lotteryCount).call().then((response) => {
          // console.log(response)
          recentWinnerstemp.push(response)
          countTemp--
          if (countTemp == 0) {
            // console.log("pppppppppp")
            console.log('Recent winners retrieved succesfully')
            setrecentWinners(recentWinnerstemp)
            // console.log(recentWinners)

          } else {
            console.log("wwwwwwwwwwwwwww")

          }
        }, (error) => {
          console.log(error)
        })

        lotteryCount--
      }

    }, (error) => {
      console.log(error)
      console.log("hhhhhhh")
    })
  }, []);
  function enterLottery() {
    console.log('attempting to enter lottery')
  }
  return (
    <div>
      <Menu id={"Home"} />
      <Header enterLottery={this.enterLottery} something={console.log('attempting to enter lottery')} />
      <table>
        <RecentWinners recentWinners={recentWinners} />
      </table>

      {/* <div className="App">
        <header className="App-header">
          <Menu />
        </header>
      </div> */}
    </div>
  );
}

export default App;
