import Winner from "./Winner";
import React, { useState, useEffect } from 'react';
import RecentWinnersHeader from "./RecentWinnersHeader";

const winnersheader = [{ address: "Address", fee: "Entrance", entrance_date: "Entrance Date", won: "Amount Won", date: "Closed" }]
const winners = [{ address: "0xCf076007E6C36cFB1eb2cD36AD66405feC7d0B37", fee: "0.1", entrance_date: "20-09-2021", won: "0.945", date: "20-09-2021" },
{ address: "0xCf076007E6C36cFB1eb2cD36AD66405feC7d0B31", fee: "0.1", entrance_date: "20-09-2021", won: "0.945", date: "20-09-2021" },
{ address: "0xCf076007E6C36cFB1eb2cD36AD66405feC7d0B32", fee: "0.1", entrance_date: "20-09-2021", won: "0.945", date: "20-09-2021" },
{ address: "0xCf076007E6C36cFB1eb2cD36AD66405feC7d0B33", fee: "0.1", entrance_date: "20-09-2021", won: "0.945", date: "20-09-2021" },
{ address: "0xCf076007E6C36cFB1eb2cD36AD66405feC7d0B34", fee: "0.1", entrance_date: "20-09-2021", won: "0.945", date: "20-09-2021" },
{ address: "0xCf076007E6C36cFB1eb2cD36AD66405feC7d0B35", fee: "0.1", entrance_date: "20-09-2021", won: "0.945", date: "20-09-2021" },
{ address: "0xCf076007E6C36cFB1eb2cD36AD66405feC7d0B36", fee: "0.1", entrance_date: "20-09-2021", won: "0.945", date: "20-09-2021" }]

function RecentWinners(props) {
    const [recentWinners, setrecentWinners] = useState(winners);
    useEffect(() => {
        // Update the document title using the browser API
        document.title = `You clicked } times`;
    }, []);
    return (
        <tbody id={"Recent winners"} className={"recent-winners"}>
            <tr className={"recent-winners-heading"}><td>Recent Winners</td></tr>
            <tr className={"recent-winners-table"} >
                {winnersheader.map((header) => {
                    // winnersheader.map
                    return (
                        <RecentWinnersHeader key={header.address.toString()} header={header} />
                    );

                })}
                {recentWinners.map((winner) => {
                    return (
                        <Winner key={winner.address.toString()} winner={winner} />
                    );

                })}
            </tr>
        </tbody>
    )
}

export default RecentWinners;
