import { useEthers } from "@usedapp/core";
import { Button, makeStyles } from "@material-ui/core"
import rejoicingman from "../rejoicing-main.png"
// import lotteryContract from "../App"
import { UseContractMethod } from "../hooks";

const useStyles = makeStyles((theme) => ({
    green: {
        background: "#00FF2A"
    },
    pink: {
        background: "#FF0077"
    },
    container: {
        padding: "72px 0px 72px 72px",
        display: "flex",
        justifyContent: "flex-start",
        gap: theme.spacing(1)
    }
}))
export const Header = (props) => {
    const classes = useStyles()
    const { activateBrowserWallet, deactivate, account } = useEthers()
    // const doit = () => {
    //     const { state, send } = useContractFunction(lotteryContract, 'enter', { transactionName: 'Wrap' })
    //     console.log(state, "||||", send, "||||||||")
    // }
    console.log('These are the props')
    console.log(props)

    // props.enterLottery()
    // function EnterLottery() {

    // }
    const isConnected = account != undefined
    const recentW = props.recentWinner
    return (
        <div className={classes.container}>


            <div className={"full-width"}>
                <div className={"header-connect"}>

                    {props.isConnected ? <button color="green" className={"btn connect-btn connected"}>
                        Connected


                    </button> :
                        <button color="primary" className={"btn connect-btn"} onClick={() => {
                            props.connectMetaMask()
                            console.log(props.isConnected)
                        }}>
                            Connect wallet


                        </button>
                    }
                    <p>Lottery State: {props.lotteryState}</p>
                </div>
                {<div className={"header-hero"}>
                    <div className={"header-hero-item1"}>
                        You can enter with at least 50 usd worth of usd

                    </div>
                    <button className={"header-hero-item2"} onClick={() => {
                        props.enterLottery()
                    }}>

                        Enter Lottery



                    </button>
                    <div className={"header-hero-item3"}>
                        <p>Lottery State: {props.lotteryState}</p>
                        <img src={rejoicingman} width={"500px"} height={"400px"} >
                        </img>
                    </div>

                </div>}
            </div>
        </div >
    )
}
