import { useState } from "react"



function Faq(props) {

    const [isOpen, setisOpen] = useState(false)
    function open() {
        setisOpen(!isOpen)
    }
    return (
        <div className={"faq"} onClick={() => {
            setisOpen(!isOpen)
            console.log(isOpen)
        }}>
            <p className={"question"}>{props.faq.question}</p>
            <div className={isOpen ? "answer hide" : "answer"}>{props.faq.answer}</div>
        </div>
    )
}

export default Faq
